215|  private shouldRetry(
216|    error: AxiosError,
217|    request: InternalAxiosRequestConfig & { _retryCount?: number; _retryable?: boolean; }
218|  ): boolean {
219|    // Don't retry if we've hit the max retries
220|    if ((request._retryCount ?? 0) >= this.config.maxRetries) {
221|      return false;
222|    }
223|
224|    // Don't retry if explicitly marked as non-retryable
225|    if (request._retryable === false) {
226|      return false;
227|    }
228|
229|    // Only retry safe HTTP methods
230|    const method = request.method?.toLowerCase();
231|    if (method && !['get', 'head', 'options', 'put', 'delete'].includes(method)) {
232|      // Don't retry POST requests unless they have idempotency key
233|      if (method === 'post' && !request.headers['idempotency-key']) {
234|        return false;
235|      }
236|    }
237|
238|    // Retry on network errors
239|    if (!error.response) {
240|      return ['ENOTFOUND', 'ECONNRESET', 'ETIMEDOUT', 'ECONNREFUSED'].includes(error.code ?? '');
241|    }
242|
243|    // Retry on 5xx server errors and specific 4xx errors
244|    const {status} = error.response;
245|    return status >= 500 || status === 429; // Include rate limiting
246|  }
