248|  private calculateRetryDelay(retryCount: number): number {
249|    // Exponential backoff with jitter
250|    const baseDelay = this.config.retryDelay;
251|    const exponentialDelay = baseDelay * Math.pow(2, retryCount - 1);
252|    const jitter = Math.random() * 0.1 * exponentialDelay;
253|
254|    return Math.min(exponentialDelay + jitter, 30000); // Cap at 30 seconds
255|  }
