261|  private createErrorFromResponse(response: AxiosResponse): MagpieError {
262|    const { data, status, headers } = response;
263|
264|    return new MagpieError({
265|      message: data?.error?.message ?? data?.message ?? `HTTP ${status} Error`,
266|      type: this.mapStatusToErrorType(status),
267|      code: data?.error?.code ?? `http_${status}`,
268|      statusCode: status,
269|      requestId: headers['request-id'] as string,
270|      headers: headers as Record<string, any>,
271|    });
272|  }
