275|  private mapStatusToErrorType(status: number): MagpieErrorType {
276|    if (status >= 500) return 'api_error';
277|    if (status === 429) return 'rate_limit_error';
278|    if (status === 401) return 'authentication_error';
279|    if (status === 403) return 'permission_error';
280|    if (status === 404) return 'not_found_error';
281|    if (status >= 400) return 'invalid_request_error';
282|    return 'api_error'; 
